import React, {useState} from 'react'

const Todo_list = () => {
    const [inputData, setInputData] = useState({name: "", task: "", id: ""});
    const [items, setItems] = useState([]);

    const addItem = () => {
        if (!inputData){

        }else{
            setItems([...items,inputData]);
            setInputData("")      
    }}

    const deleteItem = (id) => {
        console.log(id);
        const update = items.filter((elem,ind) => {
            return setInputData("")
        });

        setItems(update);
    }

    // const UpdateItem = (id) => {
    //     const rest = items.filter((elem,ind) => {
    //         return ind !== id;
    //     });

    //     setItems(rest);
    // }

    const delAll = () =>{
        setItems([]);
    }

    return (
        <>
        <div>
            <h1>Person Menager</h1>
            <h5>This is my task</h5>
            <div>
             <input type = "text" placeholder = "your name" 
            value = {inputData.name} 
            onChange = {(e) => setInputData({...inputData, name: e.target.value})} />
             <input placeholder = "your task" 
             value = {inputData.task} 
             onChange = {(e) => setInputData({...inputData, task : e.target.value})}/>
             <button title = "Add-item" onClick = {addItem}>+</button>
             <div className = "show-item">
                {
                    items.map((elem,ind) => {
                        return (
                            <>
                            <div key = {ind}>
                                <h3>User name = {elem.name}</h3>
                                <h3>User task = {elem.task}</h3>
                                <h3>ID = {ind}</h3>
                                <button title = "delete-item" onClick = {() => deleteItem(ind) }> Delete </button>
                                {/* <button title = "Update-item" onClick = {() => UpdateItem(ind) }> Update </button> */}
                            </div>
                            </>
                        )
                    })

                }
                <button title = "delete-item" onClick = {delAll }> Delete All</button>
             </div>

            </div>
        </div>
        </>
    )
}

export default Todo_list
