import React, {useState} from 'react'
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"

const Todo_list = () => {
    const [inputData, setInputData] = useState({name: "", task: ""});
    const [items, setItems] = useState([]);
    const [toggleSubmit, setToggleSubmit ] = useState(true);
    const [isEditItem, setIsEditItem] = useState(null);
    const addItem = () => {
        if (!inputData){
            alert("fill the data")

        } else if(inputData && !toggleSubmit) {
            setItems(items.map((elem, ind) => {
                if (ind === isEditItem){
                    return { ...elem, name: inputData.name, task: inputData.task }
                }
                return elem
            }))

            setToggleSubmit(true);
            setInputData({name: "", task: ""}); 
            setIsEditItem(null)
        }
        else{
            setItems([...items,inputData]);
            setInputData({name: "", task: ""})      
    }}

    const deleteItem = (id) => {
        // console.log(id);
        const rest = items.filter((elem,ind) => {
            return ind !== id;
        });

        setItems(rest);
    }

    const editItem = (id) => {
        const newEditItem = items.find((elem, ind) => {
            return ind === id;
        });

        console.log(newEditItem);
        setInputData({name: newEditItem.name, task: newEditItem.task}); 
        setToggleSubmit(false);
        setIsEditItem(id)

    }

    // const delAll = () =>{
    //     setItems([]);
    // }

    return (
        <>
        <div>
            <h1>Person Menager</h1>
            <h5>This is my task</h5>
            <div>

             {/* <button title = "Add-item" onClick = {addItem}>add Item</button> */}
             <div className = "show-item">

                {
                    items.map((elem,ind) => {
                        return (
                            <>
                            <div key = {ind}>
                                <h3>User name = {elem.name}</h3>
                                <h3>User task = {elem.task}</h3>
                                <input type = "text" placeholder = "your name" 
            value = {inputData.name} 
            onChange = {(e) => setInputData({...inputData, name: e.target.value})} /><br></br>
             <input placeholder = "your task" 
             value = {inputData.task} 
             onChange = {(e) => setInputData({...inputData, task : e.target.value})}/><br></br>
             { 
                toggleSubmit ? <button title = "Add-item" onClick = {addItem}>Add</button> : 
                <button title = "Update-item" onClick = {addItem}>Update Item</button>
                }
                                <button title = "delete-item" onClick = {() => deleteItem(ind) }> Delete </button>
                                <button title = "Update-item" onClick = {() => editItem(ind) }> Update </button>

                            </div>
                            </>
                        )
                    })

                }
                {/* <button title = "delete-item" onClick = {delAll }> Delete All</button> */}
             </div>

            </div>
        </div>
        </>
    )
}

export default Todo_list
